mapper.select:
SELECT articleNo, categoryNo, articleTitle, articleContent, regDate
FROM kostablog.article
WHERE articleNo = #{articleNo}

SELECT category.id, article.articleNo, article.categoryNo, article.articleTitle, article.articleContent, DATE_FORMAT(article.regDate, '%Y%m%d%H%i%s') FROM kostablog.CATEGORY category, kostablog.ARTICLE article WHERE category.categoryNo = article.categoryNo AND category.id = #{userNo}
AND article.categoryNo = #{crtCateNo}
ORDER BY articleNo DESC
